// AddTransactionForm.js

import React, { useState } from "react";

function AddTransactionForm({ onSubmit }) {
  const [formData, setFormData] = useState({
    date: "",
    description: "",
    category: "",
    amount: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData); // Pass form data to the parent component for submission
    // You can also clear the form here by resetting the formData state
    setFormData({
      date: "",
      description: "",
      category: "",
      amount: 0,
    });
  };

  return (
    <div className="ui segment">
      <form className="ui form" onSubmit={handleSubmit}>
        {/* Input fields with onChange handlers */}
        {/* ... */}
        <button className="ui button" type="submit">
          Add Transaction
        </button>
      </form>
    </div>
  );
}

export default AddTransactionForm;
